function __cpk(spec, v) {
    var m = sa.mean(v);
    var sigma = sa.stdev(v);

    if(sigma === 0) {
        sigma = 0.00000000000000000001;
    }
    var u = (spec.usl - m) / (3*sigma);

    var cpk;

    if (spec.lsl === null) {
        cpk = {
            cpk: u,
            cp: u/2
        }
    } else {
        var l = (m - spec.lsl) / (3*sigma);
        cpk = {
            cpk: sm.min([l, u]),
            cp: (spec.usl - spec.lsl) / (6*sigma)
        }
    }

    if( cpk.cp === null) cpk.cp = m;
    if( cpk.cpk === null) cpk.cpk = m;

    return cpk;
}

function _cpk(cep, k, so) {

    var saved = cep.get(k);

    var val = saved.map(function(s) {
        return s.fields.val;
    });

    if (val.length <= 1) {
        val.push(so.fields.val);
    }

    var spec = {
        lsl: so.fields.min, //sm.min(que.val),
        usl: so.fields.max, //sm.max(que.val),
    };

    var c = __cpk(spec, val);

    so.fields.cp = c.cp;
    so.fields.cpk = c.cpk;
    so.fields.usl = spec.usl;
    so.fields.lsl = spec.lsl;

    return so;
}

function _store(cep, so) {
    var key = so.tags.iid + ":" + keyCH;

    so.key = key;
    so.tags.fireCNF = false;
    so.tags.fireCPK = false;

    so.fields.acc = (so.tags.pass === true) ? 0.0 : 1.0;

    var isin = cep.has(key);
    if(!isin) {
        cep.set(key, [so]);
    } else {
        var p = cep.get(key);
        so.fields.acc = (so.tags.pass === true) ? 0.0 : parseInt(p[0].fields.acc) + 1.0;

        if( p.length > 50 ) { // queue limit !
            p.pop();
        }

        p.unshift(so);
        cep.set(key, p);
    }

    flow.set("cepTABLE", cep);

    return {key:key, so:so};
}

//===========================================================================
// GET CPK
