function _alram(cep, rule, head, sos) {

    var hits = [];

    rule.forEach( (r) => {

        //node.warn(r);


        // 연속 뷸량 검출        
        var cf = r.CONT_FAIL;
        if (cf !== null && cf !== undefined) {
            var cmax = parseInt(cf.COUNT);

            sos.filter ( (so) => {
                return !(r.INSPROP_IDX === 0 || r.INSPROP_IDX === so.tags.iid);
            }).map( (so) => {

                so.fields.cmax = cmax;                
                //node.warn("test:" + cmax + " with:" +so.fields.acc + " IN: " + so.tags.facility + ":" + so.tags.channel + " : " + so.fields.inm + ": " + so.tags.model + ": " + so.tags.pass);

                if( so.fields.acc >= cmax) {

                    so.tags.fireCNF = true;

                    so.output = 1;
                    hits.push(so);

                    var p = cep.get(so.key);
                    s = p.shift();
                    s.fields.acc = 0; // accumulator clear
                    p.unshift(s);
                    cep.set(so.key, p);
                }
            })
        }

        // CPK 위반 검출
        var cpk = r.CPK;
        if (cpk !== null && cpk !== undefined) {
            sos.filter ( (so) => {
                 return !(r.INSPROP_IDX === 0 || r.INSPROP_IDX === so.tags.iid);
            }).map( (so) => {
                if( (so.fields.cpk < cpk.LSL) || (so.fields.cpk > cpk.USL) ) {
                    so.output = 2;
                    so.fields.lsl_min = cpk.LSL;
                    so.fields.usl_max = cpk.USL;                    
                    so.fireCPK = true;
                    hits.push(so);
                }
            });
        }        
    });

    return hits;
}
var alram = _alram(cepT, ruleT, head, proc);

